import lombok.*;

@Entity
@Table(name = "users")
@Getter
@Setter
@NoArgsConstructor
@AllArgsConstructor
@ToString(exclude = {"password"}) // prevent recursive logging
@EqualsAndHashCode(of = "id")     // safer comparison
