\nimport org.springframework.stereotype.Service;\n\nimport java.util.List;\n\n@Service\n@RequiredArgsConstructor\npublic class UserServiceImpl implements userService {\n\n    private final UserRepository userRepository;\n\n    // 🟢 Register a new user\n    @Override\n    public String registerUser(RegisterRequest request) {\n        if (userRepository.existsByEmail(request.getEmail())) {\n            throw new RuntimeException(\"Email already in use\");\n        }\n\n        User user = new User();\n        user.setName(request.getName());\n        user.setEmail(request.getEmail());\n        user.setPassword(request.getPassword());\n        user.setContactNumber(request.getContactNumber());\n        user.setAddress(request.getAddress());\n\n        userRepository.save(user);\n        return \"User registered successfully\";\n    }\n\n    // 🟢 Fetch all users\n    @Override\n    public List<User> getAllUser() {\n        return userRepository.findAll();\n    }\n\n    // 🟢 Fetch user by ID\n    @Override\n    public User getUserById(Long id) {\n        return userRepository.findById(id)\n                .orElseThrow(() -> new RuntimeException(\"User not found with ID: \" + id));\n    }\n\n    // 🟢 Delete user by ID\n    @Override\n    public void deleteUser(Long id) {\n        if (!userRepository.existsById(id)) {\n            throw new RuntimeException(\"User not found with ID: \" + id);\n        }\n        userRepository.deleteById(id);\n    }\n\n    // 🟢 Get current user by email\n    @Override\n    public User getCurrentUser(String email) {\n        return userRepository.findByEmail(email)\n                .orElseThrow(() -> new RuntimeException(\"User not found with email: \" + email));\n    }\n\n    // 🟢 Update current user by email\n    @Override\n    public User updateCurrentUser(String email, User updatedUser) {\n        User existingUser = getCurrentUser(email);\n\n        existingUser.setName(updatedUser.getName());\n        existingUser.setAddress(updatedUser.getAddress());\n        existingUser.setContactNumber(updatedUser.getContactNumber());\n        existingUser.setPassword(updatedUser.getPassword());\n\n        return userRepository.save(existingUser);\n    }\n}\n","baseTimestamp":1759818369739,"deltas":[{"timestamp":1759818378737,"changes":[{"type":"DELETE","lineNumber":2,"oldContent":"package com.prodapt.restfulapp.service.impl;"},{"type":"INSERT","lineNumber":3,"content":""}]},{"timestamp":1759818438777,"changes":[{"type":"MODIFY","lineNumber":19,"content":"    ","oldContent":"    // 🟢 Register a new user"}]},{"timestamp":1759818477791,"changes":[{"type":"MODIFY","lineNumber":19,"content":"","oldContent":"    "}]},{"timestamp":1759818557308,"changes":[{"type":"INSERT","lineNumber":32,"content":"        "}]},{"timestamp":1759818564607,"changes":[{"type":"MODIFY","lineNumber":32,"content":"        user.setRole(request.getRole()); // ✅ set role from RegisterRequest","oldContent":"        "},{"type":"INSERT","lineNumber":34,"content":""}]},{"timestamp":1759818567972,"changes":[{"type":"MODIFY","lineNumber":32,"content":"        user.setRole(request.getRole()); ","oldContent":"        user.setRole(request.getRole()); // ✅ set role from RegisterRequest"}]},{"timestamp":1759818585903,"changes":[{"type":"INSERT","lineNumber":77,"content":"        existingUser.setRole(updatedUser.getRole());"}]},{"timestamp":1759818601405,"changes":[{"type":"MODIFY","lineNumber":32,"content":"        user.setRole(request.getRole());","oldContent":"        user.setRole(request.getRole()); "}]}]},"C:/Users/subadeveswaran.v/Downloads/restfulapp/restfulapp/src/main/java/com/prodapt/restfulapp/controller/UserController.java":{"filePath":"C:/Users/subadeveswaran.v/Downloads/restfulapp/restfulapp/src/main/java/com/prodapt/restfulapp/controller/UserController.java","baseContent":"package com.prodapt.restfulapp.controller;\n\nimport org.springframework.stereotype.Controller;\n\n@Controller\n\npublic class UserController {\n}\n","baseTimestamp":