package com.prodapt.restfulapp.dto;

import lombok.*;

@Data
@NoArgsConstructor
@AllArgsConstructor
public class AddRouteRequest {
    private String origin;
    private String destination;
    private String distanceInKm;
    private String duration; // e.g., "2h 45m"
}

package com.prodapt.restfulapp.service;

import com.prodapt.restfulapp.dto.AddRouteRequest;
import com.prodapt.restfulapp.entity.Route;
import com.prodapt.restfulapp.repository.RouteRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.List;
import java.util.stream.Collectors;

@Service
public class RouteServiceImpl implements RouteService{
    @Autowired
    public RouteRepository routeRepository;
    @Override
    public String addRoute(AddRouteRequest routeDTO) {
        Route route = new Route(
                null,
                routeDTO.getOrigin(),
                routeDTO.getDestination(),
                routeDTO.getDistanceInKm(),
                routeDTO.getDuration()
        );
        routeRepository.save(route);
        if(route!=null){
            return "Route added successfully";
        }else{
            return "Route not found";
        }
    }

    @Override
    public List<AddRouteRequest> getAllRoutes() {
        return routeRepository.findAll().stream().map(route ->
                new AddRouteRequest(
                        route.getOrigin(),
                        route.getDestination(),
                        route.getDistanceInKm(),
                        route.getDuration()
                )).collect(Collectors.toList());
    }

    @Override
    public String updateRoute(Long id, AddRouteRequest routeDTO) {
        Route route = routeRepository.findById(id)
                .orElseThrow(() -> new RuntimeException("Route not found"));
        route.setOrigin(routeDTO.getOrigin());
        route.setDestination(routeDTO.getDestination());
        route.setDistanceInKm(routeDTO.getDistanceInKm());
        route.setDuration(routeDTO.getDuration());
        routeRepository.save(route);
        return "Route Updated Successfully";
    }

    @Override
    public String deleteRoute(Long id) {
        Route route = routeRepository.findById(id)
                .orElseThrow(() -> new RuntimeException("Route not found"));
        routeRepository.deleteById(id);
        return "Route Deleted successfully";
    }
}

package com.prodapt.restfulapp.entity;

import com.fasterxml.jackson.annotation.JsonIgnore;

import jakarta.persistence.*;
import lombok.*;

@Entity
@Table(name = "seats")
@Data
@NoArgsConstructor
@AllArgsConstructor
public class Seat {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @Column(nullable = false)
    private String seatNumber;

    @Column(nullable = false)
    private boolean isBooked;

    @Column(nullable = false)
    private String status;

    @ManyToOne(fetch = FetchType.LAZY, optional = false)
    @JoinColumn(name = "flight_id", nullable = false, foreignKey = @ForeignKey(name = "fk_seat_flight_id", foreignKeyDefinition = "FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE CASCADE"))
    @JsonIgnore
    private Flight flight;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "booking_id")
    @JsonIgnore
    private Booking booking; // nullable when not booked
}
